<?php$sql = $wpdb->prepare("	SELECT S.*, H.*	FROM {$wpdb->prefix}mcc_sch_settings AS S	INNER JOIN {$wpdb->prefix}mcc_sch_history AS H ON(sch_id=h_sch_id)	ORDER BY h_outTime DESC"); //WHERE sch_uId={$UserID} AND sch_type='content_order'$all_order = $wpdb->get_results($sql);?><link rel="stylesheet" href="//code.jquery.com/ui/1.11.2/themes/smoothness/jquery-ui.css"><script type="text/javascript" src="//code.jquery.com/ui/1.11.2/jquery-ui.js"></script><script type="text/javascript" src="<?php echo site_url(); ?>/data-table/jquery.dataTables.min.js"></script><link rel="stylesheet" href="<?php echo site_url(); ?>/data-table/jquery.dataTables.css"/><style type="text/css">    thead th{text-transform: none !important;font-size: 12px;}	tbody td{text-align:center}</style><div class="accoSet">    <h2 class="fulllist">All Email Report History</h2></div><div class="clear_both"></div><div class="item-delivery">	<div class="clear_both"></div>    <table style="font-size:92%!important;width:100%!important;" id="example" class="display" cellspacing="0" >        <thead style="background-color: #888;color:white;">            <tr>                <th style="width:3%;text-align: center;">#ID</th>                <th>Email To</th>                <th>Frequency</th>                <th>Report</th>                <th>Expected Out Time</th>                <th>Emailed Out Time</th>				<th>Report Link</th>            </tr>        </thead>        <tbody>            <?php            if (!empty($all_order)) {                foreach ($all_order as $in => $r_order) {                    ?>                    <tr style="text-align:center;font-size: 13px;">                        <td style="width:5%;text-align: center;"><?php echo $r_order->h_id; ?></td>                        <td><?php echo $r_order->h_emailTo; ?></td>						<td><?php echo $r_order->sch_frequency; ?></td>						<td>                                                    <?php                                                    $report_full_name = $wpdb->get_row("SELECT * FROM `wp_all_email_report` WHERE `short_name` = '$r_order->sch_type'")->report_name;                                                    echo $report_full_name; ?>                                                </td>						<td><?php echo $r_order->sch_outTime; ?></td>						<td><?php echo $r_order->h_outTime; ?></td>                        <td>                            <?php                             $report_link = site_url().'/'.$r_order->h_reportLink;                             if($r_order->h_id <= 163){                                $report_link = home_url('/').'wp-content/reports/order-content/'.$r_order->h_reportLink;                             }                            ?>                            <a style="text-decoration:none;" target="_blank" href="<?php echo $report_link; ?>"><?php echo str_replace('pdf/schedule-report/','',$r_order->h_reportLink); ?></a></td>                    </tr>                    <?php                }            }            ?>        </tbody>    </table></div><script type="text/javascript">jQuery(document).ready(function($){	$('#example').dataTable({		"order": [[0, "desc"]],		"iDisplayLength": 25	});});</script>